s.PK_SAWON_CODE = #{PK_SAWON_CODE} and s.sawon_name = d.daily_TA_SAWON_NAME
and d.pk_DAILY_TA_WORKING_DATE = #{PK_DAILY_TA_WORKING_DATE}
select distinct s.sawon_name, s.fk_dept_name, s.rank_name from com_loginout c, sawon s ,daily_ta d where c.userno = s.pk_sawon_code and c.userno = #{PK_SAWON_CODE,jdbcType=VARCHAR}
select dept_name from dept
select distinct d.pk_DAILY_TA_WORKING_DATE, d.daily_TA_SAWON_NAME, d.daily_TA_RANK, d.daily_TA_DEPT_NAME, d.daily_TA_GO_TIME, d.daily_TA_OFF_TIME, d.daily_TA_WORK_TIME, d.daily_TA_EXTEN_WORK, d.daily_TA_NIGHT_WORK, d.daily_TA_LATE_TIME, d.daily_TA_LEAVE_TIME from DAILY_TA d, sawon s, dept p
PK_DAILY_TA_WORKING_DATE between #{PK_DAILY_TA_WORKING_DATE2} and #{PK_DAILY_TA_WORKING_DATE3}
and s.PK_SAWON_CODE = #{sawon_num} and s.sawon_name = d.daily_TA_SAWON_NAME
and daily_ta_dept_name = #{option}
update DAILY_TA set DAILY_TA_SAWON_NAME = #{daily_TA_SAWON_NAME,jdbcType=VARCHAR}, DAILY_TA_RANK = #{daily_TA_RANK,jdbcType=VARCHAR} , DAILY_TA_DEPT_NAME = #{daily_TA_DEPT_NAME,jdbcType=VARCHAR} , DAILY_TA_TA = #{daily_TA_TA,jdbcType=VARCHAR}, DAILY_TA_GO_TIME = #{daily_TA_GO_TIME,jdbcType=VARCHAR} , DAILY_TA_OFF_TIME = #{daily_TA_OFF_TIME,jdbcType=VARCHAR} , DAILY_TA_WORK_TIME = #{daily_TA_WORK_TIME,jdbcType=VARCHAR}, DAILY_TA_HOLI_WORK = #{daily_TA_HOLI_WORK,jdbcType=VARCHAR} , DAILY_TA_EXTEN_WORK = #{daily_TA_EXTEN_WORK,jdbcType=VARCHAR} , DAILY_TA_NIGHT_WORK = #{daily_TA_NIGHT_WORK,jdbcType=VARCHAR}, DAILY_TA_LATE_TIME = #{daily_TA_LATE_TIME,jdbcType=VARCHAR} , DAILY_TA_LEAVE_TIME= #{daily_TA_LEAVE_TIME,jdbcType=VARCHAR} where PK_DAILY_TA_WORKING_DATE = #{pk_DAILY_TA_WORKING_DATE, jdbcType=VARCHAR}
insert into daily_TA (pk_DAILY_TA_WORKING_DATE,daily_TA_SAWON_NAME, daily_TA_DEPT_NAME, daily_TA_RANK,daily_TA_GO_TIME, daily_TA_OFF_TIME, daily_TA_WORK_TIME, daily_TA_EXTEN_WORK, daily_TA_LATE_TIME, daily_TA_LEAVE_TIME) values(#{pk_DAILY_TA_WORKING_DATE,jdbcType=VARCHAR}, #{daily_TA_SAWON_NAME,jdbcType=VARCHAR}, #{daily_TA_RANK,jdbcType=VARCHAR}, #{daily_TA_DEPT_NAME,jdbcType=VARCHAR}, #{daily_TA_GO_TIME,jdbcType=VARCHAR}, #{daily_TA_OFF_TIME,jdbcType=VARCHAR}, #{daily_TA_WORK_TIME,jdbcType=VARCHAR}, #{daily_TA_EXTEN_WORK,jdbcType=VARCHAR}, #{daily_TA_LATE_TIME,jdbcType=VARCHAR}, #{daily_TA_LEAVE_TIME,jdbcType=VARCHAR})
update daily_ta set (DAILY_TA_SAWON_NAME = DAILY_TA_RANK, DAILY_TA_DEPT_NAME) select SAWON_NAME, RANK_NAME,FK_RANK_NAME FROM SAWON
delete daily_ta where pk_DAILY_TA_WORKING_DATE = #{pk_DAILY_TA_WORKING_DATE} and daily_TA_SAWON_NAME = #{daily_TA_SAWON_NAME}
