mapper.select:
 
		
      
   select s.sawon_NAME, s.rank_NAME, s.fk_DEPT_NAME,s.pk_SAWON_CODE
    from sawon s
     
		

			
				
					and s.FK_DEPT_NAME = #{sawon_num}
				
				
					and s.PK_SAWON_CODE = #{sawon_num}
				
				
					and s.SAWON_NAME = #{sawon_num}

select distinct d.pk_DAILY_TA_WORKING_DATE, d.daily_TA_GO_TIME, d.daily_TA_OFF_TIME, d.daily_TA_WORK_TIME, d.daily_TA_EXTEN_WORK, d.daily_TA_LATE_TIME, d.daily_TA_LEAVE_TIME, d.fk_daily_ta_sawon_code, d.daily_TA_END,d.num, s.fk_DEPT_NAME, s.rank_NAME, s.sawon_NAME from DAILY_TA d, sawon s
TO_CHAR(TO_DATE(substr(pk_daily_ta_working_date,1,7), 'YYYY-MM'), 'YYYYMM') = #{fd_year} and s.pk_sawon_code = d.fk_daily_ta_sawon_code
and s.FK_DEPT_NAME = #{sawon_num}
and s.PK_SAWON_CODE = #{sawon_num}
and s.SAWON_NAME = #{sawon_num}

select distinct d.pk_DAILY_TA_WORKING_DATE, d.daily_TA_GO_TIME, d.daily_TA_OFF_TIME, d.daily_TA_WORK_TIME, d.daily_TA_EXTEN_WORK, d.daily_TA_LATE_TIME, d.daily_TA_LEAVE_TIME, d.fk_daily_ta_sawon_code, d.daily_TA_END,d.num, s.fk_DEPT_NAME, s.rank_NAME, s.sawon_NAME from DAILY_TA d, sawon s
TO_CHAR(TO_DATE(substr(pk_daily_ta_working_date,1,7), 'YYYY-MM'), 'YYYYMM') = #{fd_year} s.pk_sawon_code = d.fk_daily_ta_sawon_code
and s.FK_DEPT_NAME = #{sawon_num}
and s.PK_SAWON_CODE = #{sawon_num}
and s.SAWON_NAME = #{sawon_num}
