mapper.select:
SELECT * FROM (SELECT ROWNUM AS NO, SD.PK_AD_NUM, AD_TITLE, SD.PK_SAWON_CODE, CU.SAWON_NAME, AD_DOCSTEP, SD.PK_DOCTYPE_NUM, SDT.DOCTYPE_DTTITLE, AD_DEPT_NAME, AD_MOD_DATE, CC.CODENM as AD_DOCSTATUS , (SELECT COUNT(*) FROM ATTACHMENT WHERE FK_ATTACHMENT_DOCU_NUM=SD.PK_AD_NUM) FILECNT FROM AD SD INNER JOIN SAWON CU ON SD.PK_SAWON_CODE=CU.PK_SAWON_CODE INNER JOIN DOCTYPE SDT ON SDT.PK_DOCTYPE_NUM=SD.PK_DOCTYPE_NUM INNER JOIN COM_CODE CC ON CC.CODECD=SD.AD_DOCSTATUS AND CLASSNO='2'
ORDER BY PK_AD_NUM DESC ) A
WHERE A.NO BETWEEN ${rowStart-1} AND ${rowStart+9}

mapper.sql:
WHERE SD.AD_DELETEFLAG='N' AND SD.PK_SAWON_CODE= '${PK_SAWON_CODE}'
AND SD.AD_DOCSTATUS IN (1,2) AND SD.AD_DOCSTATUS=${searchExt1}
${item} LIKE '%'||#{searchKeyword}||'%'

mapper.select:
SELECT * FROM (SELECT ROWNUM as NO, SD.PK_AD_NUM, AD_TITLE, SD.PK_SAWON_CODE, CU.SAWON_NAME, AD_DOCSTEP, SD.PK_DOCTYPE_NUM, SDT.DOCTYPE_DTTITLE, AD_DEPT_NAME, AD_MOD_DATE, CC.CODENM as AD_DOCSTATUS, (SELECT COUNT(*) FROM ATTACHMENT WHERE FK_ATTACHMENT_DOCU_NUM=SD.PK_AD_NUM) as FILECNT FROM AD SD INNER JOIN SAWON CU ON SD.PK_SAWON_CODE=CU.PK_SAWON_CODE INNER JOIN DOCTYPE SDT ON SDT.PK_DOCTYPE_NUM=SD.PK_DOCTYPE_NUM INNER JOIN COM_CODE CC ON CC.CODECD=SD.AD_DOCSTATUS AND CLASSNO='2'
ORDER BY PK_AD_NUM DESC ) A
WHERE A.NO BETWEEN ${rowStart-1} AND ${rowStart+9}

mapper.update:
UPDATE AD
   SET AD_TITLE=#{AD_TITLE}, AD_CONTENT=#{AD_CONTENT}, AD_DOCSIGNPATH=#{AD_DOCSIGNPATH}
     , AD_MOD_DATE=to_char(sysdate,'yyyy.mm.dd hh24:mi')
 WHERE PK_AD_NUM=#{PK_AD_NUM}

UPDATE AD
   SET AD_DELETEFLAG='Y'
 WHERE PK_AD_NUM=#{PK_AD_NUM}

mapper.delete:

       DELETE 
         FROM ATTACHMENT
        WHERE FILENO IN (
             
                    ${item}
               
       )

mapper.update:
  
        UPDATE 
		(SELECT APPROVAL_SSRESULT, APPROVAL_SIGN_DATE, APPROVAL_SSCOMMENT
		FROM AD SD, APPROVAL SS
		WHERE SD.PK_AD_NUM=SS.FK_AD_NUM
		AND SD.AD_DOCSTEP=SS.APPROVAL_SSSTEP
		AND SS.FK_AD_NUM=#{PK_AD_NUM, jdbcType=VARCHAR} 
		)
		SET APPROVAL_SSRESULT= #{APPROVAL_SSRESULT}, APPROVAL_SIGN_DATE=to_char(sysdate,'yyyy.mm.dd hh24:mi'), APPROVAL_SSCOMMENT= #{APPROVAL_SSCOMMENT}

        UPDATE AD
           SET AD_DOCSTATUS = #{APPROVAL_SSRESULT}
			
	        	, AD_DOCSTEP = AD_DOCSTEP + 1
	        
         WHERE PK_AD_NUM=#{PK_AD_NUM}

UPDATE AD
   SET AD_DOCSTATUS = 0
 WHERE PK_AD_NUM=#{PK_AD_NUM, jdbcType=VARCHAR}

mapper.select:
SELECT PK_DEPT_CODE, DEPT_NAME
  FROM DEPT
 WHERE KEY=#{deptno}

SELECT PK_SAWON_CODE, SAWON_NAME, CU.FK_DEPT_CODE, CD.DEPT_NAME, CC.CODENM RANK_NAME FROM SAWON CU INNER JOIN DEPT CD ON CD.PK_DEPT_CODE=CU.FK_DEPT_CODE INNER JOIN COM_CODE CC ON CC.CODENM=CU.RANK_NAME AND CLASSNO='3' WHERE CU.SAWON_USER_STATUS='Y'
AND CU.KEY=#{searchExt1}
AND CU.SAWON_NAME LIKE '%'||#{searchKeyword}||'%'
