mapper.select:
SELECT * FROM (SELECT ROWNUM as NO, SSTITLE, SSTYPE, SSSTARTDATE, SSSTARTHOUR, SSSTARTMINUTE, SSENDDATE, SSENDHOUR , SSENDMINUTE, SSREPEATTYPE, SSREPEATEND, SSCONTENTS, SSISOPEN, TC.PK_SAWON_CODE, SAWON_NAME FROM SCHEDULE TC INNER JOIN SAWON CU ON TC.PK_SAWON_CODE=CU.PK_SAWON_CODE
ORDER BY SSNO DESC ) A
WHERE A.NO BETWEEN ${rowStart-1} AND ${rowStart+9}

mapper.update:
UPDATE SCHEDULE
   SET SSTITLE=#{SSTITLE}, SSTYPE=#{SSTYPE}, SSSTARTDATE=#{SSSTARTDATE}, SSSTARTHOUR=#{SSSTARTHOUR}, SSSTARTMINUTE=#{SSSTARTMINUTE}, SSENDDATE=#{SSENDDATE}
     , SSENDHOUR=#{SSENDHOUR}, SSENDMINUTE=#{SSENDMINUTE}, SSREPEATTYPE=#{SSREPEATTYPE}, SSREPEATOPTION=#{SSREPEATOPTION}, SSREPEATEND=#{SSREPEATEND}
     , SSCONTENTS=#{SSCONTENTS}, SSISOPEN=#{SSISOPEN}, UPDATEDATE=to_char(sysdate,'yyyy.mm.dd hh24:mi')
 WHERE SSNO=#{SSNO}

mapper.delete:
UPDATE SCHEDULE
   SET DELETEFLAG='Y'
 WHERE SSNO=#{SSNO}

mapper.select:
SELECT CLASSNO, CODECD, CODENM
  FROM COM_CODE CC
 WHERE CLASSNO=#{CLASSNO} AND CODECD=#{CODECD}

mapper.update:
UPDATE COM_CODE
   SET CODENM=#{CODENM}
 WHERE CLASSNO=#{CLASSNO} AND CODECD=#{CODECD}

mapper.select:
SELECT PK_AD_NUM, AD_TITLE, SD.SAWON_NAME, DT.DOCTYPE_DTTITLE, AD_INT_DATE, CODENM AS AD_DOCSTATUS FROM AD AD INNER JOIN SAWON SD ON AD.PK_SAWON_CODE=SD.PK_SAWON_CODE INNER JOIN COM_CODE CC ON AD.AD_DOCSTATUS=CC.CODECD INNER JOIN DOCTYPE DT ON DT.PK_DOCTYPE_NUM=AD.PK_DOCTYPE_NUM WHERE AD_DELETEFLAG='N' AND CC.CLASSNO=2 AND AD_DOCSTATUS IN(1,2)
AND ROWNUM BETWEEN ${rowStart-1} AND ${displayRowCount}
ORDER BY PK_AD_NUM DESC
