mapper.select:
SELECT * FROM (SELECT ROWNUM as NO, SSTITLE, SSTYPE, SSSTARTDATE, SSSTARTHOUR, SSSTARTMINUTE, SSENDDATE, SSENDHOUR , SSENDMINUTE, SSREPEATTYPE, SSREPEATEND, SSCONTENTS, SSISOPEN, TC.PK_SAWON_CODE, SAWON_NAME FROM SCHEDULE TC INNER JOIN SAWON CU ON TC.PK_SAWON_CODE=CU.PK_SAWON_CODE
ORDER BY SSNO DESC ) A
WHERE A.NO BETWEEN ${rowStart-1} AND ${rowStart+9}

mapper.update:
UPDATE SCHEDULE
   SET SSTITLE=#{SSTITLE}, SSTYPE=#{SSTYPE}, SSSTARTDATE=#{SSSTARTDATE}, SSSTARTHOUR=#{SSSTARTHOUR}, SSSTARTMINUTE=#{SSSTARTMINUTE}, SSENDDATE=#{SSENDDATE}
     , SSENDHOUR=#{SSENDHOUR}, SSENDMINUTE=#{SSENDMINUTE}, SSREPEATTYPE=#{SSREPEATTYPE}, SSREPEATOPTION=#{SSREPEATOPTION}, SSREPEATEND=#{SSREPEATEND}
     , SSCONTENTS=#{SSCONTENTS}, SSISOPEN=#{SSISOPEN}, UPDATEDATE=to_char(sysdate,'yyyy.mm.dd hh24:mi')
 WHERE SSNO=#{SSNO}

mapper.delete:
UPDATE SCHEDULE
   SET DELETEFLAG='Y'
 WHERE SSNO=#{SSNO}

mapper.select:
SELECT CLASSNO, CODECD, CODENM
  FROM COM_CODE CC
 WHERE CLASSNO=#{CLASSNO} AND CODECD=#{CODECD}

mapper.update:
UPDATE COM_CODE
   SET CODENM=#{CODENM}
 WHERE CLASSNO=#{CLASSNO} AND CODECD=#{CODECD}

mapper.select:
SELECT PK_AD_NUM, AD_TITLE, SD.SAWON_NAME, DT.DOCTYPE_DTTITLE, AD_INT_DATE, CODENM AS AD_DOCSTATUS FROM AD AD INNER JOIN SAWON SD ON AD.PK_SAWON_CODE=SD.PK_SAWON_CODE INNER JOIN COM_CODE CC ON AD.AD_DOCSTATUS=CC.CODECD INNER JOIN DOCTYPE DT ON DT.PK_DOCTYPE_NUM=AD.PK_DOCTYPE_NUM WHERE AD_DELETEFLAG='N' AND CC.CLASSNO=2 AND AD_DOCSTATUS IN(1,2)
AND ROWNUM BETWEEN ${rowStart-1} AND ${displayRowCount}
ORDER BY PK_AD_NUM DESC

mapper.sql:
WHERE BRDDELETEFLAG='N' AND TBG.BGDELETEFLAG='N'

    AND TB.BGNO=#{bgno}
  

    
         ${item} LIKE '%'||#{searchKeyword}||'%'

mapper.select:
SELECT * FROM (SELECT ROWNUM as NO, BRDNO, BRDTITLE, BRDDATE, BRDLIKE , TB.FK_SAWON_CODE, CU.SAWON_NAME BRDWRITER, BGNAME, TB.BGNO , (SELECT COUNT(*) FROM BOARDREAD WHERE BRDNO=TB.BRDNO) BRDHIT , (SELECT COUNT(*) FROM BOARDFILE WHERE BRDNO=TB.BRDNO) FILECNT , (SELECT COUNT(*) FROM BOARDREPLY WHERE BRDNO=TB.BRDNO AND REDELETEFLAG='N') REPLYCNT FROM BOARD TB INNER JOIN SAWON CU ON TB.FK_SAWON_CODE=CU.PK_SAWON_CODE INNER JOIN BOARDGROUP TBG ON TBG.BGNO=TB.BGNO WHERE BRDDELETEFLAG='N' AND TBG.BGDELETEFLAG='N'
${item} LIKE '%'||#{searchKeyword}||'%'
ORDER BY BRDNO DESC )A WHERE 1=1
AND A.BGNO=#{bgno}
AND A.NO BETWEEN ${rowStart-1} AND ${rowStart+9}
ORDER BY BRDNO DESC

SELECT * FROM (SELECT ROWNUM as NO, BRDNO, BRDTITLE, BRDDATE, BRDNOTICE, BRDLIKE , TB.FK_SAWON_CODE, CU.SAWON_NAME BRDWRITER, BGNAME, TB.BGNO , (SELECT COUNT(*) FROM BOARDREAD WHERE BRDNO=TB.BRDNO) BRDHIT , (SELECT COUNT(*) FROM BOARDFILE WHERE BRDNO=TB.BRDNO) FILECNT , (SELECT COUNT(*) FROM BOARDREPLY WHERE BRDNO=TB.BRDNO AND REDELETEFLAG='N') REPLYCNT FROM BOARD TB INNER JOIN SAWON CU ON TB.FK_SAWON_CODE=CU.PK_SAWON_CODE INNER JOIN BOARDGROUP TBG ON TBG.BGNO=TB.BGNO WHERE BRDDELETEFLAG='N' AND BRDNOTICE='Y'
AND TB.BGNO=#{bgno}
ORDER BY BRDNO DESC ) A WHERE A.NO BETWEEN 1 AND 5

mapper.update:
UPDATE BOARD
   SET BRDTITLE=#{brdtitle}
     , BRDMEMO=#{brdmemo} 
     , BRDNOTICE=#{brdnotice, jdbcType=VARCHAR}
     , MOD_DATE=to_char(sysdate,'yyyy.mm.dd hh24:mi')
     , MOD_USER_ID=#{FK_SAWON_CODE}
 WHERE BRDDELETEFLAG='N'
   AND BRDNO=#{brdno}

mapper.insert:

       UPDATE BOARD
          SET BRDLIKE = BRDLIKE + 1 
        WHERE BRDNO=#{field1}

mapper.delete:
UPDATE BOARD
   SET BRDDELETEFLAG='Y'
   	 , BRDNOTICE='N'
     , MOD_DATE=to_char(sysdate,'yyyy.mm.dd hh24:mi')
     , MOD_USER_ID=#{PK_SAWON_CODE}
 WHERE BRDNO=#{brdno}


       DELETE 
         FROM BOARDFILE
        WHERE FILENO IN (
             
                    ${item}
               
       )

mapper.insert:
    
        SELECT NVL(MAX(RENO),0)+1 FROM BOARDREPLY
    

    INSERT INTO BOARDREPLY(BRDNO, RENO, FK_SAWON_CODE, REDELETEFLAG, REMEMO, REDATE, REORDER, REPARENT, REDEPTH)
    VALUES (#{brdno}, #{reno}, #{FK_SAWON_CODE}, 'N', #{rememo, jdbcType=VARCHAR}, to_char(sysdate,'yyyy.mm.dd hh24:mi'), #{reorder},
               
                   #{reno}, 0
                   #{reparent}, #{redepth}
               
           )

mapper.select:
SELECT BRDNO, REDEPTH+1 REDEPTH, COALESCE((SELECT MAX(REORDER) FROM BOARDREPLY WHERE REPARENT=TB.RENO), TB.REORDER) reorder
  FROM BOARDREPLY TB
 WHERE RENO=#{reparent}

mapper.update:

       UPDATE BOARDREPLY
          SET REORDER = REORDER + 1
        WHERE REORDER>${reorder}
          AND BRDNO=${brdno}

mapper.select:
SELECT NVL(MAX(REORDER),0)+1
  FROM BOARDREPLY
 WHERE BRDNO=#{brdno}

mapper.update:

       UPDATE BOARDREPLY
          SET REDELETEFLAG='Y' 
            , MOD_DATE=to_char(sysdate,'yyyy.mm.dd hh24:mi')
            , MOD_USER_ID=#{PK_SAWON_CODE}
        WHERE RENO=#{reno}

UPDATE BOARDREPLY
   SET REMEMO=#{rememo} 
     , MOD_DATE=to_char(sysdate,'yyyy.mm.dd hh24:mi')
     , MOD_USER_ID=#{FK_SAWON_CODE}
 WHERE RENO=#{reno}
